mapper.update:
update fenlei_baohu_main
ipci = #{ipci},
cci = #{cci},
cca = #{cca},
csets = #{csets}
where id = #{id}

mapper.select:
select m.id, mingcheng, m.simpleclasscode, (to_date(jinantime,'yyyymmddhh24miss') - to_date('1970-01-01 08:00:00','yyyy-mm-dd hh24:mi:ss'))*86400000 as jinantime, r.worker as worker, (select u.areaname from fenlei_baohu_userinfo u where u.workername = r.worker) as areaname, m.type from fenlei_baohu_main m, (select id,worker from fenlei_baohu_result where classtype = '主') r, (select workername from fenlei_baohu_userinfo where is_online = '1'
and dep1 = #{dep1}
and dep2 = #{dep2}
) u where m.state = #{state} and r.id = m.id and r.worker = u.workername

select oraginization, count(1) as totalCount, sum(decode(type,'FM',1,'XX',0)) as fmCount, sum(decode(type,'FM',0,'XX',1)) as xxCount, sum(decode(state,'0',1,0)) as wfpCount, sum(decode(state,'1',1,0)) as wccCount, sum(decode(state,'2',1,0)) as ywcCount, sum(decode(state,'7',1,0)) as cjCount from FENLEI_BAOHU_MAIN
where jinantime between #{begintime} and #{endtime}
group by oraginization

select m.id from fenlei_baohu_main m where 1=1

  AND m.sqh = #{sqh}


    AND m.mingcheng = #{mingcheng}
;

select m.id,m.mingcheng,m.type,m.sqr,m.sqh,m.pdf_path,m.state,m.simpleclasscode,m.message,m.oraginization

 from FENLEI_BAOHU_MAIN m where 1=1

    AND m.id = #{id}


    AND m.sqh = #{sqh}


    AND m.mingcheng = #{mingcheng}

select count(1) from FENLEI_BAOHU_MAIN where 1=1

    AND id=#{id}


    AND oraginization=#{oraginization}

select m.id,m.mingcheng,m.type,m.sqr,m.sqh,m.pdf_path pdfPath,m.state,m.simpleclasscode,m.message,m.oraginization, (to_date(jinantime,'yyyymmddhh24miss') - to_date('1970-01-01 08:00:00','yyyy-mm-dd hh24:mi:ss'))*86400000 as jinantime, (to_date(chuantime,'yyyymmddhh24miss') - to_date('1970-01-01 08:00:00','yyyy-mm-dd hh24:mi:ss'))*86400000 as chuantime from fenlei_baohu_main m where 1=1
AND m.state = #{state}
ORDER BY jinantime desc

select oraginization, count(1) as totalCount, sum(decode(type, 'FM', 1, 'XX', 0)) as fmCount, sum(decode(type, 'FM', 0, 'XX', 1)) as xxCount from FENLEI_BAOHU_MAIN where state = '2'
and chuantime between #{begintime} and #{endtime}
group by oraginization

mapper.update:
update fenlei_baohu_main
set
    state = #{state}
where
    id = #{id}
