mapper.select:
select substr(r.worker,1,6) as fldm, substr(r.worker,8) as worker, u.dep1, u.dep2, u.orgname, r.id, m.mingcheng, u.email, r.worker as fldmworker, m.simpleclasscode, m.jinantime, r.fenpeitime from fenlei_baohu_result r left join fenlei_baohu_main m on r.id = m.id left join fenlei_baohu_userinfo u on substr(r.worker,1,6) = u.loginname where r.fenpeiren = '进案' and r.id in
#{item}
order by fldm asc

select t.worker, substr(t.worker,1,instr(t.worker,'-',1)-1) fldm, substr(t.worker,instr(t.worker,'-',1)+1) workerName, (select concat(u.dep1, u.dep2) from FENLEI_BAOHU_USERINFO u where u.workername = t.worker) as dep1, (select u.areaname from FENLEI_BAOHU_USERINFO u where u.workername = t.worker) as areaName, sum(decode(a.type,'FM',decode(t.classtype, '主',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 0, 1), 0), 0)) fmzfNumCount, sum(decode(a.type,'FM',decode(t.classtype, '副',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 0, 1), 0), 0)) fmffNumCount, sum(decode(a.type,'FM',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 1, 0), 0)) fmNoNumCount, sum(decode(a.type,'XX',decode(t.classtype, '主',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 0, 1), 0), 0)) xxzfNumCount, sum(decode(a.type,'XX',decode(t.classtype, '副',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 0, 1), 0), 0)) xxffNumCount, sum(decode(a.type,'XX',decode(coalesce(t.ipci, t.ipca, t.ipcmi,t.ipcoi, t.cci,t.cca,t.csets), NULL, 1, 0), 0)) xxNoNumCount, (select count(distinct id) from FENLEI_BAOHU_ADJUDICATION ad where ad.processingperson = t.worker) cjNum, (select count(distinct id) from FENLEI_BAOHU_ADJUDICATION ad where (ad.adjudicator like '%' || t.worker || '%')) cjyNum
from fenlei_baohu_main a, fenlei_baohu_result t where a.id = t.id
AND t.chuantime between #{begintime} and #{endtime}
AND t.classtype=#{type}
AND substr(t.worker,instr(t.worker,'-')+1)=#{userName}
AND t.worker in (select workername from FENLEI_BAOHU_USERINFO where orgname =#{dept})
group by t.worker

select r.id,r.worker,r.classtype as maintype,r.fenpeiren,r.state,r.cci,r.cca,r.csets,r.ipci,r.ipca,r.ipcmi,r.ipcoi,
m.mingcheng,m.type,m.sqr,m.simpleclasscode,m.pdf_path,
(to_date(r.fenpeitime,'yyyymmddhh24miss') - to_date('1970-01-01 08:00:00','yyyy-mm-dd hh24:mi:ss'))*86400000 as jinantime,
(to_date(r.chuantime,'yyyymmddhh24miss') - to_date('1970-01-01 08:00:00','yyyy-mm-dd hh24:mi:ss'))*86400000 as chuantime
from fenlei_baohu_result r,fenlei_baohu_main m
where r.worker like concat('%',#{user}) and r.id=m.id

    AND r.state = #{state} and r.classtype = #{classtype}


    AND r.state != '2' and r.classtype = #{classtype}


    AND r.state = #{state}


    AND r.chuantime between #{begintime} and #{endtime}

select r.worker from fenlei_baohu_result r where
r.state in ('0','1','9')
and r.id=#{id}

mapper.update:
update fenlei_baohu_result


        state = #{state}


where id = #{id} and worker = #{worker}
